import axios from 'axios';
export default {
  name: 'QueryControls',
  props: ["pointsOfInterest", "routes", "global"],
  data: function data() {
    return {
      travelMode: null,
      interestKeyword: null,
      startPositionLocation: null,
      endPositionLocation: null
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.$eventHub.$on('startPositionChange', function (data) {
      _this.startPositionLocation = data;
    });
    this.$eventHub.$on('endPositionChange', function (data) {
      _this.endPositionLocation = data;
    });
  },
  methods: {
    query: function query() {
      var waypointPointsOfInterest = [];
      this.pointsOfInterest.forEach(function (c) {
        if (c.isInPlan) {
          waypointPointsOfInterest.push({
            timeToSpend: c.timeSpent * 3600,
            place_id: c.place.place_id
          });
        }
      });
      var getBody = {
        startPosition: this.startPositionLocation,
        endPosition: this.endPositionLocation,
        travelMode: this.travelMode,
        waypointPointsOfInterest: waypointPointsOfInterest,
        pointsOfIntereseKeyword: this.interestKeyword
      };
      var vm = this;
      vm.routes.interestingPointsInRoute = [];
      axios.get('http://127.0.0.1:8090/routes', {
        body: getBody
      }).then(function (response) {
        vm.routes.fastestRoute = response.data;
      }).catch(function (e) {
        console.log("Error in request: " + e);
      });
      axios.get('http://127.0.0.1:8090/routes2', {
        body: getBody
      }).then(function (response) {
        vm.routes.interestingRoute = response.data;
        response.data.pointsOfInterestNearRoute.forEach(function (c) {
          var service = new window.google.maps.places.PlacesService(vm.global.map);
          service.getDetails({
            placeId: c
          }, function (place, status) {
            if (status === window.google.maps.places.PlacesServiceStatus.OK) {
              vm.routes.interestingPointsInRoute.push({
                location: {
                  lat: place.geometry.location.lat(),
                  lng: place.geometry.location.lng()
                },
                place: place,
                isInPlan: false
              });
            }
          });
        });
      }).catch(function (e) {
        console.log("Error in request: " + e);
      });
    }
  }
};
